sys_push_notif_msg_content.script:
(function buildJSON(/*GlideRecord*/ current, /*String*/ message, /*Object*/ attributes) {

 var json = {};

 var layoutFieldGenerator = new global.NotificationLayoutFieldGenerator();
    var identifier = layoutFieldGenerator.layoutField(current.sys_class_name, current.sys_id, "number");
    var description = layoutFieldGenerator.layoutField(current.sys_class_name, current.sys_id, "short_description");
    var status = layoutFieldGenerator.layoutField(current.sys_class_name, current.sys_id, "priority");

	var deepLinkGenerator = new global.MobileDeepLinkGenerator("request");//request = now mobile app / agent = Agent mobile app
	var link = deepLinkGenerator.getFormScreenLink("181c92e81b0550101167ff37dc4bcbf6", current.getTableName(), current.getValue("sys_id"));
	attributes.number = identifier;

	json = {
        "aps" : {
            "sound" : "default"
        },
		"Link": link,
        "Layout" : {
             "Status": status,
             "Identifier" : identifier,
             "Description" : description
        }
    };

 return json;

})(current, message, attributes);
